package dev.yafatek.annotations;import java.lang.annotation.ElementType;import java.lang.annotation.Retention;import java.lang.annotation.RetentionPolicy;import java.lang.annotation.Target;@Target(ElementType.FIELD)@Retention(RetentionPolicy.RUNTIME)public @interface Url {	/**	 * Method to hold the api url sent by the user to the ApiService Object.	 * <p>	 * this method will call the api to make the call to the End point.	 *	 * @return String represents the api url.	 */	String url() default "http://localhost:8080/";}