package dev.yafatek.apicalls;import retrofit2.Call;import retrofit2.http.GET;import retrofit2.http.Path;import java.util.List;public interface ApiOperations<T> {	String GET_REQUEST = "lookup-customer?customer-detail";	String REQUEST_PARAM = "customer-detail";	@GET(GET_REQUEST)	Call<List<T>> getData(@Path(REQUEST_PARAM) String lookupCustomer);}