package dev.yafatek.apicalls;import dev.yafatek.apptests.RestModel;import retrofit2.Call;import retrofit2.http.GET;import retrofit2.http.Query;import java.util.List;public interface ApiOperations {	String GET_REQUEST = "lookup-customer?customer-detail";	String REQUEST_PARAM = "customer-detail";	@GET(GET_REQUEST)	Call<List<RestModel>> getData(@Query(REQUEST_PARAM) String lookupCustomer);}