package dev.yafatek.apicalls;import retrofit2.Retrofit;import retrofit2.converter.gson.GsonConverterFactory;public final class ApiService {	private String baseUrl;	public static void initApiCalls() {		Retrofit retrofit = new Retrofit.Builder()				.baseUrl("https://api.github.com")				.addConverterFactory(GsonConverterFactory.create())				.build();	}}