package dev.yafatek.models;public final class ApiClientResponse {	private boolean status;	private String message;	private Object response;	public ApiClientResponse(boolean status, String message, Object response) {		this.status = status;		this.message = message;		this.response = response;	}	public ApiClientResponse() {	}	public boolean isStatus() {		return status;	}	public void setStatus(boolean status) {		this.status = status;	}	public String getMessage() {		return message;	}	public void setMessage(String message) {		this.message = message;	}	public Object getResponse() {		return response;	}	public void setResponse(Object response) {		this.response = response;	}}