package dev.yafatek.networking;import dev.yafatek.models.ApiClientResponse;/** * Builder interface to initialize the  Api Client process. */public interface ApiClientBuilder {	/**	 * Builder Method to Create Customized Implementation Class	 *	 * @param clazz class Name to be init	 * @param <E>   Type of implementation Class	 * @return instance of the Class	 * @throws Exception Throw Exception if we Can't Create the Object.	 */	static <E> E builder(Class<E> clazz) throws Exception {		return clazz.getDeclaredConstructor().newInstance();	}	/**	 * builder To Init the Process With default implementation of the Interface.	 *	 * @return ApiClient Object witch is the Default implementation of hte interface	 */	static ApiClient buildWithDefaults() {		return ApiClient.apiClientBuilder();	}	/**	 * getting the api url	 *	 * @param apiUrl api url string	 * @return this object	 */	ApiClientBuilder url(String apiUrl);	/**	 * init the Client	 */	ApiClientBuilder inti();	/**	 * perform operation	 */	ApiClientBuilder perform();	/**	 * retrieve the Data.	 */	ApiClientResponse retrieve();}