package dev.yafatek.networking;import java.io.IOException;/** * interface To Map the Basic Http requests used by the MicroService Call. * * @param <T> Http Methods. * @author Feras Alawadi */public interface HttpMethods<T> {	/**	 * method to perform Get request.	 *	 * @return object of type T	 * @throws IOException io exception.	 */	T get() throws IOException;	/**	 * Method to perform post request.	 *	 * @return object of type T	 */	T post();	/**	 * method to perform patch request	 *	 * @return object of type T	 */	T patch();	/**	 * method to preform put request.	 *	 * @return object of type T	 */	T put();	/**	 * method to perform delete request	 *	 * @return object of type T	 */	T delete();	/**	 * method to perform update request.	 *	 * @return object of type T.	 */	T update();}