package dev.yafatek.operations;import java.util.List;/** * Interface to Create a Microservice using the abstract Factory pattern. * * @param <T> */public interface MicroService<T> {	/**	 * Perform a Get request  to get the Data from Microservice	 */	T getFromMicroService();	/**	 * get From MicroService With query params	 *	 * @param params query parameters	 * @return Object of the Microservice	 */	T getFromMicroService(Object params);	/**	 * Perform a Post request to  MicroService.	 *	 * @param params object Holds the Data to a MicroService	 * @return response	 */	T postToMicroService(Object params);	List<T> postToMicroServiceRespToList(Object param);	T deleteFromMicroService(Object params);	T updateInMicroService(Object params);}